;=======================================================================================================================
```	This fills in the cache of the overscroll area so that when the screen scrolls onto new tiles,
        they can be copied across in a fast and straight-forward fashion.
	```
@params IY`vars         ``Address of the common variables (used throughout)
        ;---------------------------------------------------------------------------------------------------------------
        ;scrolling enabled??
        ;TODO: this could be located at the call site (macro?) to avoid the wasted `call`+`ret`
        bit     5       [IY`vars+%vars.flags0]
        ret     z
        
        ;interrupts are disabled so that tiles do not get written to the screen between updating them
        di
        ;switch pages 1 & 2 ($4000-$BFFF) to banks 4 & 5 ($10000-$17FFF)
        ld      A`bank                  :blocksS1_BlockMappings@bank
        ld      [%sms.mapper.slot1]    A`bank
        ld      [$.SLOT1]               A`bank
        ld      A`bank                  :blocksS1_BlockMappings@bank+1
        ld      [%sms.mapper.slot2]    A`bank
        ld      [$.SLOT2]               A`bank
        ei
        
        ;---------------------------------------------------------------------------------------------------------------
        ;get the address of the solidity data for the level's tilemap:
        ;TODO: we should just store the solidity data adress in the level header, instead of an index
        
        ld      A       [$.LEVEL_SOLIDITY]                      ;get the solidity index for the level
        add     A       A                                       ;double it (for a pointer)
        ld      C       A                                       ;and put it into a 16-bit number (BC)
        ld      B       $00
        
        ;look up the index in the solidity pointer table
        ld      HL      :mobsS1_Solidity_Blocks
        add     HL      BC
        
        ;load an address at the table
        ld      A       [HL]
        inc     HL
        ld      H       [HL]
        ld      L       A
        
        ;store the solidity data address in RAM
        ld      [$.TEMP3]       HL
        
        ;---------------------------------------------------------------------------------------------------------------
        ;horizontal scrolling allowed??
        bit     0       [IY`vars+%vars.flags2]
        jp      z       .vert                                   ;skip to vertical scroll handling
        
        ;has the camera moved left?
        bit     6       [IY`vars+%vars.flags0]
        jr      nz      .horz
        
        ld      B       $00
        ld      C       $08
        jp      ._1

        ;get the position in the floor layout (in RAM) of the camera:
        
.horz   ld      A       [$.VDPSCROLL_HORZ]
        and     %00011111                                       ;MOD 32 (i.e. 0-31 looping)
        add     A       8                                       ;add 8 (ergo, 8-39)
        rrca                                                    ;divide by 2 ...
        rrca                                                    ;... 4
        rrca                                                    ;... 8
        rrca                                                    ;... 16
        rrca                                                    ;... 32
        and     %00000001                                       ;remove everything but bit 0
        ld      B       $00                                     ;load result into BC
                                                                ;-- either $0000 or $0001
        ld      C       A

._1     call    getFloorLayoutRAMPosition
        
        ;---------------------------------------------------------------------------------------------------------------
        ld      A       [$.VDPSCROLL_HORZ]
        
        ;has the camera moved left?
        bit     6       [IY`vars+%vars.flags0]
        jr      z       ._2
        add     A       8
        
        ;which of the four tiles width in a block is on the left-hand side of the screen
        ;- that is, determine which column within a block the camera is on
._2     and     %00011111                                       ;MOD 32 (limit to pixels within block)
        srl     A                                               ;divide by 2 ...
        srl     A                                               ;divide by 4 ...
        srl     A                                               ;divide by 8 (determine tile, 0-3)
        ld      C               A                               ;copy the tile number (0-3) into BC
        ld      B               $00
        ld      [$.TEMP1]       BC                              ;stash it away for later
        
        exx
        ld      DE'     $.OVERSCROLLCACHE_HORZ
        exx
        ld      DE      [$.LEVEL_FLOORWIDTH]
        
        ld      B       7
.loopH  ld      A       [HL]                                    ;read block index from the FloorLayout
        
        exx
        ld      C'      A
        ld      B'      $00
        ld      HL'     [$.TEMP3]                               ;retrieve the solidity data address
        add     HL'     BC'                                     ;offset block index into solidity data
        
        ;multiply the block index by 16
        ;(blocks are each 16 bytes long)
        rlca                                                    ;x2 ...
        rlca                                                    ;x4 ...
        rlca                                                    ;x8 ...
        rlca                                                    ;x16
        ld      C'      A
        and     %00001111                                       ;MOD 16
        ld      B'      A
        ld      A       C'                                      ;return to the block index * 16 value
        xor     B'
        ld      C'      A
        
        ld      A       [HL']                                   ;read solidity data for block index
        rrca
        rrca
        rrca
        and     %00010000
        
        ld      HL'     [$.TEMP1]                               ;retrieve column number of VDP scroll
        add     HL'     BC'
        ld      BC'     [$.BLOCKMAPPINGS]                       ;get address of level's block mappings
        add     HL'     BC'
        ld      BC'     4
        ldi                                                     ;copy the first byte
        
        ld      [DE']   A
        inc     E'
        add     HL'     BC'
        ldi
        
        ld      [DE']   A
        inc     E'
        inc     C'
        add     HL'     BC'
        ldi
        
        ld      [DE']   A
        inc     E
        inc     C'
        add     HL'     BC'
        ldi
        
        ld      [DE']   A
        inc     E'
        
        exx
        add     HL      DE
        djnz    .loopH
        
        ;---------------------------------------------------------------------------------------------------------------
        
.vert   bit     1       [IY`vars+%vars.flags2]
        jp      z       .exit
        
        bit     7       [IY`vars+%vars.flags0]                  ;camera moved up?
        jr      nz      ._3
        
        ld      B       $06
        ld      C       $00
        jp      ._4
        
._3     ld      B       $00
        ld      C       B
        
        ;---------------------------------------------------------------------------------------------------------------
        
._4     call    getFloorLayoutRAMPosition
        ld      A       [$.VDPSCROLL_VERT]
        and     %00011111
        srl     A
        and     %11111100
        ld      C       A
        ld      B       $00
        ld      [$.TEMP1]       BC
        
        exx
        ld      DE'     $.OVERSCROLLCACHE_VERT
        exx
        
        ld      B       $09

.loopV  ld      A       [HL]
        
        exx
        ld      C'      A
        ld      B'      $00
        ld      HL'     [$.TEMP3]
        add     HL'     BC'
        rlca
        rlca
        rlca
        rlca
        ld      C'      A
        and     %00001111
        ld      B'      A
        ld      A       C'
        xor     B'
        ld      C'      A
        ld      A       [HL']
        rrca
        rrca
        rrca
        and     %00010000
        ld      HL'     [$.TEMP1]
        add     HL'     BC'
        ld      BC'     [$.BLOCKMAPPINGS]
        add     HL'     BC'
        ldi
        ld      [DE']   A
        inc     E'
        ldi
        ld      [DE']   A
        inc     E'
        ldi
        ld      [DE']   A
        inc     E'
        ldi
        ld      [DE']   A
        inc     E'
        exx
        
        inc     HL
        djnz    .loopV
        
.exit   ret
	;
	
	
