;=======================================================================================================================
```     This routine is only called during level loading to populate the screen with the visible portion of the
        Floor Layout. Scrolling fills in only the new tiles, so a full refresh of the screen is not required.
	```
        ;---------------------------------------------------------------------------------------------------------------
        ;interrupts are disabled during this routine due to it writing to the display
        di
        
        ;page in the Block Mappings, these are the 4x4 tile combinations that make up the Floor / Level
        ld      A`bank                  :blocksS1_BlockMappings@bank
        ld      [%sms.mapper.slot1]    A`bank
        ld      [$.SLOT1]               A`bank
        ld      A`bank                  :blocksS1_BlockMappings@bank + 1
        ld      [%sms.mapper.slot2]    A`bank
        ld      [$.SLOT2]               A`bank
        
        ld      BC      $0000
        call    getFloorLayoutRAMPosition
        
        ;---------------------------------------------------------------------------------------------------------------
        ld      DE      %sms.vram.screen
        ;in 192-line mode the screen is 6 blocks tall,
        ;in 224-line mode it's 7 blocks tall
        ld      B       !SMS.SCREEN.HEIGHT.BLOCKS
        
._1     push    BC  HL  DE
        ld      B       !SMS.SCREEN.WIDTH.BLOCKS
        
._2     push    BC  HL  DE
        
        ;get the block index at the current location in the Floor Layout
        ld      A       [HL]
        
        exx
        ld      E'      A                                       ;copy the block index to E
        ld      A       [$.LEVEL_SOLIDITY]                      ;load A with level's solidity index
        add     A       A                                       ;double it (i.e. for a 16-bit pointer)
        ld      C'      A                                       ;put it into BC'
        ld      B'      $00
        ;get address of solidity pointer list
        ld      HL'     :mobsS1_Solidity_Blocks
        add     HL'     BC'                                     ;offset solidity index into the list
        ld      A       [HL']                                   ;read the data pointer into HL'
        inc     HL'
        ld      H'      [HL']
        ld      L'      A
        ld      D'      $00                                     ;DE' is the block index
        add     HL'     DE'                                     ;offset block index into solidity data
        ld      A       [HL']                                   ;and get the solidity value
        
        ;in the solidity data, bit 7 determines that the tile should appear in front of sprites.
        ;rotate the byte three times to position bit 7 at bit 4. this byte will form the high-byte of the 16-bit
        ;value for the name table entry (bit 4 will therefore become bit 12 in a 16-bit number)
        rrca
        rrca
        rrca
        
        ;bit 12 of a name table entry specifies if the tile should appear in front of sprites.
        ;allow just this bit if it's set
        and     %00010000
        ld      C'      A
        exx
        
        ;return the block index to HL
        ld      L`blockIndex    [HL]
        ld      H`blockIndex    $00
        ;block mappings are 16 bytes each
	;TODO: make the number of shifts here based !BLOCK.SIZE?
	;(add     HL`blockIndex   HL`blockIndex) x !BLOCK.SIZE
        add     HL`blockIndex   HL`blockIndex                   ;x2 ...
        add     HL`blockIndex   HL`blockIndex                   ;x4 ...
        add     HL`blockIndex   HL`blockIndex                   ;x8 ...
        add     HL`blockIndex   HL`blockIndex                   ;x16
        ld      BC`blockMaps    [$.BLOCKMAPPINGS]
        add     HL`blockIndex   BC`blockMappings
        
        ;DE will be the address of block mapping
        ;HL will be an address in the screen name table
        ex      DE      HL
        
        ;---------------------------------------------------------------------------------------------------------------
        ld      B       4                                       ;4 rows of the block mapping
        
        ;set the screen name address
._3     ld      A                               L
        out     [%sms.ports.vdp.control]        A
        ld      A       H
        or      %01000000
        out     [%sms.ports.vdp.control]        A
        
        ld      A       [DE]
        out     [%sms.ports.vdp.data]   A
        inc     DE
        
        exx
        ld      A       C'
        exx
        
        out     [%sms.ports.vdp.data]   A
        nop
        nop
        ld      A       [DE]
        out     [%sms.ports.vdp.data]   A
        inc     DE
        
        exx
        ld      A       C'
        exx
        
        out     [%sms.ports.vdp.data]  A
        nop
        nop
        ld      A       [DE]
        out     [%sms.ports.vdp.data]   A
        inc     DE
        
        exx
        ld      A       C'
        exx
        out     [%sms.ports.vdp.data]   A
        nop
        nop
        ld      A       [DE]
        out     [%sms.ports.vdp.data]   A
        inc     DE
        
        exx
        ld      A       C'
        exx
        
        out     [%sms.ports.vdp.data]   A
        ld      A       B
        ld      BC      64
        add     HL      BC
        ld      B       A
        djnz    ._3
        
        pop     DE
        pop     HL
        inc     HL
        ld      BC      $0008
        ex      DE      HL
        add     HL      BC
        ex      DE      HL
        pop     BC
