        xor     A`zero                                          ;set A to zero
        ld      [$.VDPSCROLL_HORZ]      A`zero
        ld      [$.VDPSCROLL_VERT]      A`zero
        
        ;load the palette
        ld      HL      .S1_TitleScreen_Palette
