		;
        
        ;initialise the animation parameters?
        xor     A`zero
        ld      [$.D216]        A`zero                          ;reset the screen counter
        ld      A               $01
        ld      [$.TEMP2]       A
        ld      HL              ._1372
        ld      [$.TEMP3]       HL
        
