;=======================================================================================================================
```	Reads in a list of mob IDs and their positions within the level.
	```
@params HL              ``Address of a mob layout list
        ;---------------------------------------------------------------------------------------------------------------
        ;NOTE: $.D2F2 is used only here -- perhaps a regular temp variable could be used
        
	;immediately put aside a copy of the mob layout list address
        push    HL`mobList
        
        ;add Sonic to the list of active mobs first
        ld      IX`mob	$.SONIC
        ld      DE      %mob@size				;=$001A (length of the mob?)
        ld      C       $00					;?
        ld      HL      [$.D216]                                ;= D32E + (level number * 2)
        ld      A       mobPointers.sonic@index		;=0
        call    loadMobFromList
        
	;return to the mob layout list originally provided
        pop     HL`mobList
        
        ;---------------------------------------------------------------------------------------------------------------
        ld      A`mobCount	[HL`mobList]			;first byte is the number of mobs to load
        inc     HL`mobList
        
        ld      [$.D2F2]        A`mobCount			;put aside the number of mobs in the layout
        dec     A`mobCount					;reduce by 1,
        ld      B`mobCount	A`mobCount			;and set as the loop counter
        
        ;loop over the number of mobs:
._1     ld      A`mobType	[HL`mobList]			;read the mob type
        inc     HL`mobList					;move on to the X & Y position
        call    loadMobFromList
        djnz    ._1
        
        ;---------------------------------------------------------------------------------------------------------------
        
        ld      A`mobCount	[$.D2F2]			;retrieve number of mobs in layout
        ld      B`mobCount      A`mobCount
        ld      A       $20
        sub     B
        ret     z                                               ;exit if exactly 32 mobs!
        
        ;remove the remaining mobs (out of 32)
        ld      B       A
._2     ld      [IX`mob+%mob.type]      $FF                     ;remove mob?
        add     IX      DE
        djnz    ._2
        
        ret
	;

loadMobFromList:												;$235E
;=======================================================================================================================
@params IX`mob          ``Address of the mob structure to be setup
        A               ``Mob type
        HL`mobLayout	``address with the X & Y byte Block-positions of the mob on the Floor
	DE`mobSize	``size of the mob structure (to skip to the next one)
@return	IX`mob		``IX will be updated to be pointing to the next mob structure in RAM
	HL`mobLayout	``The pointer to the mob layout list will have been moved forward to the next mob in the list
        ;--------------------------------------------------------------------------------------------------------------- 
        ld      [IX`mob+%mob.type]      A`mobType		;set the mob type
        
	;x position:
        ;---------------------------------------------------------------------------------------------------------------
        ld      A`xpos	[HL`mobLayout]				;get X position from the mob layout
        exx							;put aside parameters and switch to shadow registers
        ;convert X-pos to 16-bit number in HL
	ld      L'xpos	A`xpos
        ld      H'xpos	$00
	;align the mob to whole pixels (not sub-pixels)
        ld      [IX`mob+%mob.Xsubpixel] H'zero
        
	;multiply by 32: (expand Blocks to pixels)
        add     HL'xpos	HL'xpos                                 ;x2 ...
        add     HL'xpos	HL'xpos                                 ;x4 ...
        add     HL'xpos	HL'xpos                                 ;x8 ...
        add     HL'xpos	HL'xpos                                 ;x16 ...
        add     HL'xpos	HL'xpos                                 ;x32
        
	;set the pixel X-position of the mob on the Floor
	ld      [IX`mob+%mob.X+0]       L'xpos
        ld      [IX`mob+%mob.X+1]       H'xpos
        
	;y position:
        ;---------------------------------------------------------------------------------------------------------------
	exx     						;return to original parameters
	inc     HL`mobLayout					;move over the X-position to the Y-position
        ld      A`ypos	[HL`mobLayout]				;get the Y position from the mob layout
        exx     						;return to the shadow registers
	;convert X-pos to 16-bit number in HL
        ld      L'ypos	A`ypos
        ld      H'ypos  $00
	;align the mob to whole pixels (not sub-pixels)
        ld      [IX`mob+%mob.Ysubpixel]	H'zero
        
	;multiply by 32: (expand Blocks to pixels)
	add     HL'ypos	HL'ypos                              	;x2 ...
        add     HL'ypos	HL'ypos                              	;x4 ...
        add     HL'ypos	HL'ypos                              	;x8 ...
        add     HL'ypos	HL'ypos                              	;x16 ...
        add     HL'ypos	HL'ypos                              	;x32
        
	;set the pixel Y-position of the mob on the Floor
	ld      [IX`mob+%mob.Y+0]       L'ypos
        ld      [IX`mob+%mob.Y+1]       H'ypos
        
	;set the rest of the mob structure to 0:
        ;---------------------------------------------------------------------------------------------------------------
        ;TODO: sizes used here need to be calculated directly off of the `%mob` type
	
	;transfer IX (mob address) to HL
        push    IX`mobData
        pop     HL'mobData
	
        ;skip to the 7th byte of the mob: `.xpseed`, skipping type/x/y-pos,
	;this assumes a contiguous order
        ld      DE'skip		%mob.Xspeed			;=7
        add     HL'mobData	DE'skip
        
        ;erase the next 19 bytes (remainder of mob data structure)
        ld      B'count		19
        xor     A`zero                                          ;set A to 0
.loop   ld      [HL'mobData]	A`zero
        inc     HL'mobData
        djnz    .loop
        
	;return to the original parameters
        exx
        inc     HL`mobLayout					;move to the beginning of the next mob in the list
        add     IX`mobData	DE`mobsize			;move to the next mob structure in memory
								;TODO: this number can simply be hard-coded
        ret
	;

