mobs_updateCamera:                                                                                                   ;$2F66
;=======================================================================================================================
        ;called only by "refresh"
        
@params IY`vars         ``Address of the common variables (used throughout)
        ;---------------------------------------------------------------------------------------------------------------
        ;if scrolling is locked, do nothing
        ;TODO: we could do this test at the call site instead and avoid the wasted call/ret?
        bit     6       [IY`vars+%vars.timeLightningFlags]
        ret     nz
        
        ;does the camera need to be moved horizontally toward a target?
        ld      HL`gotoX        [$.CAMERA_X_GOTO]
        ld      A`gotoXLo       L`gotoXLo
        or      H`gotoXHi
        call    nz      .scrollCameraTo_horizontal
        
        ;does the camera need to be moved vertically toward a target?
        ld      HL`gotoY        [$.CAMERA_Y_GOTO]
        ld      A`gotoYLo       L`gotoYLo
        or      H`gotoYHi
        call    nz      .scrollCameraTo_vertical
        
        ;coalesce scroll zones:
        ;---------------------------------------------------------------------------------------------------------------
        ;     
        ;       .-------------------------.  
        ;       |  +-------------------+  |     The default Scroll Zone (1) expands or contracts
        ;       |  |2.+-------------+  |  |     to fit the temporary override Scroll Zone (2)
        ;       |  |  |1.           |  |  |
        ;       |  |<-|             |->|  |
        ;       |  |  |             |  |  |
        ;       |  |  +-------------+  |  |
        ;       |  +-------------------+  |
        ;       '-------------------------'
        
        ;manage the region within which Sonic can be before scrolling the screen?
        
        ld      HL`scrollZoneOverride   [$.SCROLLZONE_OVERRIDE_LEFT]	;=32
        ld      DE`scrollZoneLeft       [$.SCROLLZONE_LEFT]		;=96
        and     A`clearCarry                                    ;clear flags, particularly carry
        sbc     HL`scrollZoneOverride   DE`scrollZoneLeft
        ;cause the DE value to head towards equality with HL?
        call    nz      ._315e
        ;---------------v
        ;       jr      c	._1
        ;       inc     DE
        ;       ret
        ;       
        ;._1     dec     DE
        ;       ret
        
        ld      [$.SCROLLZONE_LEFT]     DE
        
        ld      HL                  [$.SCROLLZONE_OVERRIDE_RIGHT]	;=72
        ld      DE                  [$.SCROLLZONE_RIGHT]		;=136
        and     A`clearCarry                                    ;clear flags, particularly carry
        sbc     HL                      DE
        call    nz      ._315e
        ld      [$.SCROLLZONE_RIGHT]    DE
        
        ld      HL                  [$.SCROLLZONE_OVERRIDE_TOP]		;=48
        ld      DE                  [$.SCROLLZONE_TOP]			;=96
        and     A`clearCarry                                    ;clear flags, particularly carry
        sbc     HL                      DE
        call    nz      ._315e
        ld      [$.SCROLLZONE_TOP]      DE
        
        ld      HL                  [$.SCROLLZONE_OVERRIDE_BOTTOM]	;=48
        ld      DE                  [$.SCROLLZONE_BOTTOM]		;=112
        and     A`clearCarry                                    ;clear flags, particularly carry
        sbc     HL                  DE
        call    nz      ._315e
        ld      [$.SCROLLZONE_BOTTOM]   DE
        
        ;check left-hand scroll zone:
        ;---------------------------------------------------------------------------------------------------------------
        ;     
        ;       > CameraX  > ScrollZoneX
        ;       .----------+--------------.
        ;       |          |              |
        ;       |  scroll  |              |
        ;       |ZoneOffset|              |
        ;       |--------->|              |
        ;       |          |              |
        ;       |  scroll  |              |
        ;       |   Zone   |              |
        ;       '----------+--------------'
        
        ld      BC`scrollZoneOffset     [$.SCROLLZONE_LEFT]
        ld      DE`sonicX               [$.SONIC.X]
        ld      HL`cameraX              [$.CAMERA_X]
        add     HL`cameraX              BC`scrollZoneOffset
        and     A`clearCarry                                    ;clear flags, particularly carry
        sbc     HL`scrollZoneX          DE`sonicX
        ;if the player is outside of the left-hand scroll zone, skip forward --
        ;(we'll have to check the right-hand scroll zone next)
        jr      c       .scrollZoneRight
        
        ;limit the camera to a maximum of 8 pixels per frame: if it could go faster,
        ;then more than one columm of scroll tiles would be required
        
        ;HL > 255?
        ;TODO: we could use `XOR A` instead of `AND A` above to reset A,
        ;      allowing us to use just `AND H` below to do the zero-check quicker
        ld      A`scrollZoneDistance    H`scrollZoneDistance
        and     A`scrollZoneDistance
        jr      nz      .limitScrollLeft
        
        ld      A`scrollZoneDistance    L`scrollZoneDistance
        cp      9                                               ;TODO: shouldn't this be 8?
        jr      c       ._2
        
.limitScrollLeft
        ;limit scroll speed to 8
        ld      HL`scrollSpeed  $0008
        
        ;---------------------------------------------------------------------------------------------------------------
        
        ;is the camera auto-scrolling to the right?
._2     bit     3       [IY`vars+%vars.scrollRingFlags]
        jr      nz      .levelLeftLimit
        
        ;is camera set to smooth scrolling?
        bit     5       [IY`vars+%vars.scrollRingFlags]
        jr      z	._3                                     ;if not, skip ahead
        
        ;smooth scrolling: scroll only 1 pixel at a time
        ld      HL`scrollSpeed  $0001
        
._3     ex      DE`scrollSpeed  HL`blank
        ;move camera X position
        ld      HL`cameraX      [$.CAMERA_X]
        and     A`clearCarry
        sbc     HL`cameraX      DE`scrollSpeed
        ;skip if moving the camera would under/overflow
        jr      c       .levelLeftLimit
        ;commit the new camera position
        ld      [$.CAMERA_X]        HL
        
        ;TODO: this could be a `jr`
        jp      .levelLeftLimit
        
        ;check right-hand scroll zone:
        ;---------------------------------------------------------------------------------------------------------------
.scrollZoneRight
        ;       * CameraX       * ScrollZoneX
        ;       .---------------+---------.
        ;       |               |         |
        ;       |     scroll    |         |
        ;       |   ZoneOffset  |         |
        ;       |-------------->|         |
        ;       |               |         |
        ;       |               |  scroll |
        ;       |               |   Zone  |
        ;       '---------------+---------'
        
        ld      BC`scrollZoneOffset     [$.SCROLLZONE_RIGHT]
        ld      HL`cameraX              [$.CAMERA_X]
        add     HL`cameraX              BC`scrollZoneOffset
        and     A`clearCarry                                    ;clear the carry flag
        sbc     HL`scrollZoneX          DE`sonicX
        ;if the player is outside of the right-hand scroll zone, skip forward
        jr      nc      .levelLeftLimit
        
        ;within right-hand scroll zone:
        ;flip all the bits in HL?
        ;TODO: WHY??? Why wouldn't a simple zero check suffice?
        ld      A                   L`scrollZoneX
        cpl     
        ld      L                   A
        ld      A                   H`scrollZoneX
        cpl     
        ld      H                   A
        
        inc     HL
        ld      A                   H
        and     A
        jr      nz      .limitScrollRight
        
        ld      A                   L
        cp      $09
        jr      c       ._6
        
.limitScrollRight
        ;limit scroll speed to 8
        ld      HL`scrollSpeed  $0008
        
        ;is the camera auto-scrolling to the right?
._6     bit     3       [IY`vars+%vars.scrollRingFlags]
        ;yes? skip ahead
        jr      nz      .levelLeftLimit
        
        ;is camera set to smooth scrolling?
        bit     5       [IY`vars+%vars.scrollRingFlags]
        jr      z       .checkOverflow
        
        ;smooth scrolling: scroll only 1 pixel at a time
        ld      HL`scrollSpeed  $0001
        
.checkOverflow
        ;ensure that the camera position won't under/overflow
        ld      DE`cameraX      [$.CAMERA_X]
        add     HL`scrollSpeed  DE`cameraX
        jr      c       .levelLeftLimit
        
        ld      [$.CAMERA_X]        HL
        
        ;camera cannot go past left edge of level:
        ;---------------------------------------------------------------------------------------------------------------
        ;note that a Level is a sub-portion of a Floor Layout (more than one Level can be packed on a Floor Layout),
        ;therefore the left-hand edge of the level is not necessarily 0
.levelLeftLimit
        ld      HL`cameraX      [$.CAMERA_X]
        ld      DE`levelLeft    [$.LEVEL_LEFT]
        and     A`clearCarry
        sbc     HL`cameraX      DE`levelLeft
        jr      nc      .levelRightLimit
        
        ;stop the camera at the level boundary
        ld      [$.CAMERA_X]    DE`levelRight
        jr      .cameraY
        
        ;camera cannot go past right edge of level:
        ;---------------------------------------------------------------------------------------------------------------
.levelRightLimit
        ld      HL`cameraX      [$.CAMERA_X]
        ld      DE`levelRight   [$.LEVEL_RIGHT]
        and     A`clearCarry
        sbc     HL`cameraX      DE`levelRight
        jr      c       .cameraY
        
        ;stop the camera at the level boundary
        ld      [$.CAMERA_X]    DE`levelRight
        
.cameraY
        ;---------------------------------------------------------------------------------------------------------------
        ;is the camera waving up and down?
        bit     6       [IY`vars+%vars.scrollRingFlags]
        call    nz      ._3164
        
        ld      BC`scrollZoneTop        [$.SCROLLZONE_TOP]
        ld      DE`sonicY               [$.SONIC.Y]
        ld      HL`cameraY              [$.CAMERA_Y]
        
        ;is the camera waving up and down?
        bit     6       [IY`vars+%vars.scrollRingFlags]
        ;-- if so, the top scroll zone is set to a fixed value
        call    nz      .updateCamera_scrollZone_waving         ;=`ld BC $0020`
        
        ;is the camera prevented from scrolling down?
        bit     7       [IY`vars+%vars.scrollRingFlags]
        ;-- if so, the top scroll zone is set to a fixed value
        call    nz      .updateCamera_scrollZone_noDown         ;=`ld BC $0070`
        
        ;get the absolute position of the top scroll zone on the Floor Layout
        add     HL`cameraY      BC`scrollZoneTop
        
        ;is the camera prevented from scrolling down?
        bit     7       [IY`vars+%vars.scrollRingFlags]
        ;-- if so, increase the scroll zone further
        call    z       .updateCamera_scrollZone_increase
        
        and     A`clearCarry
        sbc     HL`cameraY      DE`sonicY
        jr      c       ._13
        
        ld      C               $09
        
        ld      A               H
        and     A
        jr      nz      ._10
        
        ;is the camera waving up and down?
        bit     6       [IY`vars+%vars.scrollRingFlags]
        call    nz      ._311f
        
        ld      A       L
        cp      C
        jr      c       ._11
        
._10    dec     C
        ld      L                   C
        ld      H                   $00
        
        ;is the camera prevented from scrolling down?
._11    bit     7       [IY`vars+%vars.scrollRingFlags]
        jr      z       ._12
        
        srl     H
        rr      L
        bit     1       [IY`vars+%vars.unknown0]
        jr      nz      ._12
        
        ld      HL              $0000
        
._12    ex      DE              HL
        ld      HL`cameraY      [$.CAMERA_Y]
        and     A`clearCarry
        sbc     HL`cameraY      DE
        jr      c       .levelTopLimit
        
        ld      [$.CAMERA_Y]    HL
        jp      .levelTopLimit
        
        ;---------------------------------------------------------------------------------------------------------------
        
._13    ld      BC`scrollZoneBottom     [$.SCROLLZONE_BOTTOM]
        ld      HL`cameraY              [$.CAMERA_Y]
        add     HL`cameraY              BC`scrollZoneBottom
        
        ;is the camera prevented from scrolling down?
        bit     7       [IY`vars+%vars.scrollRingFlags]
        ;- if so, increase the scroll zone further
        call    z       .updateCamera_scrollZone_increase
        
        and     A`clearCarry
        sbc     HL`cameraY      DE
        jr      nc      .levelTopLimit
        
        ld      A                   L
        cpl     
        ld      L                   A
        ld      A                   H
        cpl     
        ld      H                   A
        inc     HL
        ld      C                   $09
        ld      A                   H
        and     A
        jr      nz      ._14
        
        bit     6       [IY`vars+%vars.scrollRingFlags]         ;up-down wave scrolling?
        call    nz      ._311f
        ld      A                   L
        cp      C
        jr      c       ._15
        
._14    dec     C
        ld      L                   C
        ld      H                   $00
        
._15    bit     4       [IY`vars+%vars.scrollRingFlags]         ;auto scroll up?
        jr      nz      .levelTopLimit
        
        ld      DE`cameraY      [$.CAMERA_Y]
        add     HL              DE
        jr      c       .levelTopLimit
        
        ld      [$.CAMERA_Y]    HL
        
        ;camera cannot go past top edge of level:
        ;---------------------------------------------------------------------------------------------------------------
.levelTopLimit
        ld      HL`cameraY      [$.CAMERA_Y]
        ld      DE`levelTop     [$.LEVEL_TOP]
        and     A`clearCarry
        sbc     HL`cameraY      DE`levelTop
        jr      nc      .levelBottomLimit
        ;stop the camera at the level boundary
        ld      [$.CAMERA_Y]    DE`levelTop
        
        ;camera cannot go past bottom edge of level:
        ;---------------------------------------------------------------------------------------------------------------
.levelBottomLimit
        ld      HL`cameraY      [$.CAMERA_Y]
        ld      DE`levelBottom  [$.LEVEL_BOTTOM]
        and     A`clearCarry
        sbc     HL`cameraY      DE`levelBottom
        jr      c       .ret                                    ;TODO: use `ret c`?
        ;stop the camera at the level boundary
        ld      [$.CAMERA_Y]    DE`levelBottom
        
.ret    ret
        
        ;ancillary functions:
        
._311a                                                                                                          ;$311A
        ;===============================================================================================================
        ;TODO: this seems very inefficient and should be a macro instead 
        ld      [HL]                D
        dec     HL
        ld      [HL]                E
        inc     HL
        ret
        
._311f                                                                                                          ;$311F
        ;===============================================================================================================
        ;TODO: this seems very inefficient and should be a macro instead
        ld      C                   $08
        ret
        
.scrollCameraTo_vertical                                                                                        ;$3122
        ;===============================================================================================================
        ;scroll vertically towards the locked camera position
        ld      DE`ypos             [$.LEVEL_TOP]
        and     A
        sbc     HL                  DE`ypos
        ret     z
        jr      c	.up
        
        ;scroll downwards
        inc     DE`ypos
        ld      [$.LEVEL_TOP]       DE`ypos
        ld      [$.LEVEL_BOTTOM]    DE`ypos
        ret
        
        ;scroll upwards
.up     dec     DE`ypos
        ld      [$.LEVEL_TOP]       DE`ypos
        ld      [$.LEVEL_BOTTOM]    DE`ypos
        ret

.scrollCameraTo_horizontal                                                                                      ;$3140
        ;===============================================================================================================
        ;scroll horizontally towards the locked camera position
        ld      DE                  [$.LEVEL_LEFT]
        and     A                                               ;reset carry so it doesn't affect `sbc`
        sbc     HL                  DE
        ret     z                                               ;if HL = DE then return -- no change
        jr      c	._1a                                        ;is DE > HL?
        
        inc     DE
        ld      [$.LEVEL_LEFT]      DE
        ld      [$.LEVEL_RIGHT]     DE
        ret
        
._1a    dec     DE
        ld      [$.LEVEL_LEFT]      DE
        ld      [$.LEVEL_RIGHT]     DE
        ret
        
._315e                                                                                                          ;$315E
        ;===============================================================================================================
        jr      c       ._1b
        inc     DE
        ret
        
._1b    dec     DE
        ret

._3164                                                                                                          ;$3164
        ;===============================================================================================================
        ld      HL	[$.D29D]
        ld      DE      [$.TIME]
        add     HL      DE
        ld      BC      $0200
        ld      A       H
        and     A
        jp      p	._1c
        
        neg
        ld      BC      $FE00
        
._1c    cp      $02
        jr      c	._2c
        
        ld      L       C
        ld      H       B
        
._2c    ld      [$.D29D]	HL
        ld      C	L
        ld      B       H
        ld      HL      [$.D25C]                    		;between RAM_CAMERA_X & Y
        ld      A	[$.CAMERA_Y+1]              		;high-byte of RAM_CAMERA_X
        add     HL	BC
        ld      E	$00
        bit     7   	B
        jr      z	._3c
        ld      E       $FF
._3c    adc     A       E
        ld      [$.D25C]	HL
        ld      [$.CAMERA_Y+1]	A
        ld      HL	[$.D2A1]
        ld      A       [$.D2A3]
        add     HL      BC
        adc     A       E
        ld      [$.D2A1]	HL
        ld      [$.D2A3]        A
        ld      HL		[$.D2A2]
        bit     7   	H
        jr      z	._4c
        ld      BC      $FFE0
        and     A
        sbc     HL	BC
        jr      nc	._4c
        ld      HL              $0002
        ld      [$.TIME]	HL
        ret
        
._4c    ld      HL	[$.D2A2]
        ld      BC	$0020
        and     A
        sbc     HL      BC
        ret     c
        ld      HL      	$FFFE
        ld      [$.TIME]        HL
        ret
        
.updateCamera_scrollZone_waving                                                                                 ;$31CF
        ;===============================================================================================================
        ld      BC`scrollZoneTop        $0020
        ret
        
.updateCamera_scrollZone_noDown                                                                                 ;$31D3
        ;===============================================================================================================
        ld      BC`scrollZoneTop	$0070
        ret
        
.unused_31d7                                                                                                    ;$31D7
        ;===============================================================================================================
        ld      BC`unused		$0070
        ret
        
.updateCamera_scrollZone_increase                                                                               ;$31DB
        ;===============================================================================================================
        ;not applicable with up-down wave scrolling
        bit     6	[IY`vars+%vars.scrollRingFlags]
        ret     nz
        
        ld      BC	[$.D2B7]
        add     HL      BC
        
        ret
	;

checkMobsOutOfBounds:                                                                                           ;$31E6
;=======================================================================================================================
```	Check active mobs to see if they have moved too far off-screen and need to be despawned.
        To avoid slow down, only four mobs are checked per frame.
```	
	;called only by `refresh`, could be inlined?
        ;---------------------------------------------------------------------------------------------------------------
        ;check only 4 mobs per frame:
        
        ld      A`frame             [$.FRAMECOUNT]
        and     %00000111                                       ;"MOD 8"
        ;TODO: a look-up table for this multiplication should be faster
        ld      C`multiply          A`frame
        ld      HL`fourMobs         $0068                       ;=104 (size of 4 mob structures)
                                                                ;TODO: calculate this dynamically
        call    \\math\multiply                                ;multiply 104 by the frame number 0-7
        
        ld      DE`mobAddr          $.SONIC                     ;address of first mob's data
        add     HL`fourMobs         DE`mobAddr                  ;offset to the chosen group of 4 mobs
        ex      DE`mobAddr          HL`fourMobs                 ;put this aside for now
        
        ;skips through the list of current mobs 4 at a time
        ;i.e. mob number 0, 4, 8, 12, 16, 20, 24, 28
        
        ;TODO: Could transfer the "A MOD 8" result via register I to avoid having to redo this calculation
        ld      A`frame             [$.FRAMECOUNT]
        and     %00000111                                       ;"MOD 8"
        add     A`mobIndex          A`mobIndex                  ;x 2
        add     A`mobIndex          A`mobIndex                  ;x 4
        add     A`mobIndex          A`mobIndex                  ;x 8
        
        ld      C`mobOffset         A`mobIndex
        ld      B`mobOffset         $00
        ld      HL`mobPointer       $.ACTIVEMOBS                ;list of current mob pointers
        add     HL`mobPointer       BC`mobOffset
        
        ld      C`zero              B`zero                      ;this will be used to remove mobs
        ld      B`counter           4                           ;load loop counter for 4 mobs
        
        ;fetch the mob's boundary data:
        ;---------------------------------------------------------------------------------------------------------------
        
.loop   ld      A`mobID	[DE`mobAddr]                		;get the mob ID (which type it is)
        cp      $56                                             ;> length of the mob code pointers list?
        jp      nc	.removeMob                              ;skip this loop
        
        ;switch the mob address to IX
        push    DE`mobAddr
        pop     IX`mobAddr
        
        ;swap BC/DE/HL with their shadow values
        exx
        
        ;TODO: perhaps a lookup table could be faster here
        add     A`dataOffset        A`mobID                     ;double the mob ID
        ld      L'dataOffset        A`dataOffset                ;put this into HL'
        ld      H'dataOffset        $00
        add     HL'dataOffset       HL'dataOffset               ;ID x 4
        add     HL'dataOffset       HL'dataOffset               ;ID x 8, i.e. 8 bytes per ID
        
        ld      DE'dataTable        mobBounds
        add     HL'dataOffset       DE'dataTable                ;offset into the table, 8 bytes per ID
        
        ;load BC with the first WORD, the maxmimum distance left of the camera the mob can go before despawning
        ld      C'leftLimit         [HL'data]
        inc     HL'data
        ld      B'leftLimit         [HL'data]
        
        ;copy the remaining data into the temporary space
        inc     HL'data
        ld      DE'temp             $.TEMP1
        ;BUG: this reduces BC' by 6, likely unintended as the leftLimit value
        ;does not already factor this in and does not benefit from it
        ldi
        ldi
        ldi     
        ldi     
        ldi     
        ldi
        
        ;left & right bounds:
        ;---------------------------------------------------------------------------------------------------------------
        
        ;if the camera is near the left edge of the Floor Layout then the mob's area of off-screen allowance will
        ;overhang void-space. we check for this to avoid miscalculation further down the line
        ld      HL'cameraX          [$.CAMERA_X]
        xor     A`zero                                          ;reset carry flag
        sbc     HL'cameraX          BC'leftLimit     
        jr      nc	.x
        
        ;the camera is at the left-most side of the floor,
        ;so don't underflow and believe the camera is at the far-right instead
        ld      L'cameraX           A`zero
        ld      H'cameraX           A`zero
        xor     A`zero
        
        ;has the mob gone too far left of the camera?
.x      ld      E'mobXpos           [IX`mob+%mob.X+0]
        ld      D'mobXpos           [IX`mob+%mob.X+1]
        sbc     HL'leftLimit        DE'mobXpos
        jp      nc	.removeMobExx                               ;if so, remove it
        
        ;the next WORD is how far right of the camera the mob can go before despawning.
        ;note that this value has the width of the screen included
        ;TODO: would adding 256 to the left limit be faster / good enough?
        ld      HL'rightLimit       [$.TEMP1]
        ld      BC'cameraX          [$.CAMERA_X]
        add     HL'rightLimit       BC'cameraX
        xor     A`zero                                          ;reset carry flag
        sbc     HL'rightLimit       DE'mobXpos                  ;has the mob gone too far right?
        jp      c	.removeMobExx                               ;if so, remove it
        
        ;top & bottom bounds:
        ;---------------------------------------------------------------------------------------------------------------
        
        ;if the camera is near the top edge of the Floor Layout then the mob's area of off-screen allowance will
        ;overhang void-space. we check for this to avoid miscalculation further down the line
        ld      HL'cameraY          [$.CAMERA_Y]
        ld      BC'topLimit         [$.TEMP3]
        sbc     HL'cameraY          BC'
        jr      nc	.y 
        
        ;the camera is at the top-most side of the floor, 
        ;so don't underflow and believe the camera is at the bottom instead
        ld      L'cameraY           A`zero
        ld      H'cameraY           A`zero
        xor     A`zero
        
.y      ;has the mob gone too far above the camera?
        ld      E'mobYpos           [IX`mob+%mob.Y+0]
        ld      D'mobYpos           [IX`mob+%mob.Y+1]
        sbc     HL'                 DE'mobYpos
        jp      nc	.removeMobExx                               ;if so, remove it
        
        ;the next WORD is how far below the camera the mob can go before despawning.
        ;note that this value has the height of the screen included
        
        ;TODO: would adding 192 to the bottom limit be faster / good enough?
        ;      also this would help with dynamically supporting 224-lines
        
        ld      HL'bottomLimit      [$.TEMP4]
        ld      BC'cameraY          [$.CAMERA_Y]
        add     HL'bottomLimit      BC'cameraY
        xor     A`zero
        sbc     HL'bottomLimit      DE'mobYpos
        jp      c	.removeMobExx
        
        ;---------------------------------------------------------------------------------------------------------------
        
        ;return to the non-shadow BC/DE/HL values
        exx
        
        ;TODO: why do we need to write the same pointer back again?
        ld      [HL`mobPointer]     E`mobAddr
        inc     HL`mobPointer
        ld      [HL`mobPointer]     D`mobAddr
        inc     HL`mobPointer
        
        ;move on to the next mob to process
        push    HL`mobPointer
        ld      HL`dataSize         $001A                       ;=26, size of a mob structure
        add     HL`dataSize         DE`mobAddr
        ex      DE`dataSize         HL`mobAddr
        pop     HL`mobPointer
        
        djnz    .loop
        
        ret
        
        ;remove the out-of-bounds mob!
        ;---------------------------------------------------------------------------------------------------------------
        
.removeMobExx
        ;return to the non-shadow BC/DE/HL values
        exx
        
.removeMob
        ;zero out the pointer to the mob in the active mob list
        ld      [HL`mobPointer]     C`zero
        inc     HL`mobPointer
        ld      [HL`mobPointer]     C`zero
        inc     HL`mobPointer
        
        ;move on to the next mob to process
        push    HL`mobPointer
        ld      HL`dataSize         $001A                       ;=26, size of a mob structure
        add     HL`dataSize         DE`mobAddr
        ex      DE`dataSize         HL`mobAddr
        pop     HL`mobPointer
        
        ;TODO: djnz cannot be used here probably because of >-128 relative jump.
        ;      reorganisation of code might solve this
        dec     B`coutner
        jp      nz	.loop
        
        ret
	;

processMobs:													;$392B
;=======================================================================================================================
```	Runs the code for each of the mobs in memory.
	```
@params IY`vars         ``Address of the common variables (used throughout)
        ;---------------------------------------------------------------------------------------------------------------
        ;starting from $D37E (we skip Sonic), read pointers until a non-zero one is found,
        ;or 31 pointers have been read
        ld      HL                  $.ACTIVEMOBS+2
        ld      B                   31
        
        ;read the pointer into DE
.loop   ld      E                   [HL]
        inc     HL
        ld      D                   [HL]
        inc     HL
        
        ;is the pointer non-zero?
        ld      A                   E
        or      D
        call    nz  processMob					;if so process as a mob
        
        ;keep reading memory until either something non-zero is found or we hit $D3BC
        djnz    .loop
        
        ;---------------------------------------------------------------------------------------------------------------
        
        ld      A                   [IY`vars+%vars.spriteUpdateCount]
        ld      HL                  [$.SPRITETABLE_ADDR]
        
        push    AF  HL
        
        ;process the player:
        ld      HL                  $D024                       ;TODO: Sonic's sprite table entry (VRAM)
        ld      [$.SPRITETABLE_ADDR]    HL
        ld      DE                  $.SONIC
        call    processMob
        
        pop     HL  AF
        
        ld      [$.SPRITETABLE_ADDR]    HL
        ld      [IY`vars+%vars.spriteUpdateCount]   A
        ret
	;

processMob:													;$32C8
;=======================================================================================================================
@params DE              ``Address of a mob structure
        ;---------------------------------------------------------------------------------------------------------------
        ld      A`type  [DE`mobAddr]                            ;get mob from the list
        cp      $FF                                             ;ignore mob type $FF
        ret     z
        
        push    BC  HL
        
        ;transfer DE (address of the mob) to IX
        push    DE
        pop     IX
        
        ;double the mob type number and put it into DE
        add     A                   A
        ld      E                   A
        ld      D                   $00
        
        ;offset into the mob code pointers table
        ld      HL                  mobPointers
        add     HL                  DE
        
        ;get the mob's code address into HL
        ld      A                   [HL]
        inc     HL
        ld      H                   [HL]
        ld      L                   A
        
        ;once the mob's own code has been run, handle the common actions for all mobs
        ld      DE                  postProcessMob
        push    DE
        
        ;run the mob's code
        jp      [HL]
	;
	
postProcessMob:													;$32E2
;=======================================================================================================================
```	Once a mob has run its personal code, this routine handles things that all mobs share,
        such as moving the mob and collision with Sonic.
```
@params IX`mob          ``Address of the current mob being processed
        ;---------------------------------------------------------------------------------------------------------------
        ;move mob:
        
        ;TODO: could have a mob flag to mark as stationary and skip this bit,
        ;      alternatively, the callback set up before processing the mob could be selected based on the mob's flags
        
        ;move the mob horizontally 
        ld      E`speed         [IX`mob+%mob.Xspeed+0]
        ld      D`speed         [IX`mob+%mob.Xspeed+1]
        ld      C`dir           [IX`mob+%mob.Xdirection]
        ld      L`xpos          [IX`mob+%mob.Xsubpixel]
        ld      H`xpos          [IX`mob+%mob.X+0]
        ld      A`xpos          [IX`mob+%mob.X+1]
        add     HL`xpos         DE`speed
        adc     A`xpos          C`dir
        
        ld      [IX`mob+%mob.Xsubpixel] L`xpos
        ld      [IX`mob+%mob.X+0]       H`xpos
        ld      [IX`mob+%mob.X+1]       A`xpos
        
        ;move the mob vertically
        ld      E`speed             [IX`mob+%mob.Yspeed+0]
        ld      D`speed             [IX`mob+%mob.Yspeed+1]
        ld      C`dir               [IX`mob+%mob.Ydirection]
        ld      L`ypos              [IX`mob+%mob.Ysubpixel]
        ld      H`ypos              [IX`mob+%mob.Y+0]
        ld      A`ypos              [IX`mob+%mob.Y+1]
        add     HL`ypos             DE`speed
        adc     A`ypos              C`dir
        
        ld      [IX`mob+%mob.Ysubpixel] L`ypos
        ld      [IX`mob+%mob.Y+0]       H`ypos
        ld      [IX`mob+%mob.Y+1]       A`ypos
        
        ;does the mob interact with the floor?
        bit     5       [IX`mob+%mob.flags]
        jp      nz      ._34e6                                  ;if not skip over collision handling
        
        ;find the 'nose' of the mob, according to its direction:
        ;---------------------------------------------------------------------------------------------------------------
        
        ;divide the mob height by 2 to find its vertical middle point
        ;TODO: would it be worthwhile storing this with the mob or using a lookup table?
        ld      B`zero              $00
        ld      D`yOffset           B`zero
        ld      E`yOffset           [IX`mob+%mob.height]
        srl     E`yOffset                                       ;divide height by 2
        
        ;moving left or right?
        bit     7       [IX`mob+%mob.Xspeed+1]
        jr      nz      .facingLeft
        
.facingRight
        ;- collision will be checked with the right side of the mob
        ld      C`xOffset           [IX`mob+%mob.width]
        ld      HL`table            Unknown._411E
        jp      ._2
        
.facingLeft
        ;- collision will be checked with the left side of the mob
        ld      C`xOffset           $00                         ;TODO: B & D are already zero, could use those
        ld      HL`table            Unknown._4020
        
        ;put aside the 'nose' x-position of the mob
._2     ld      [$.TEMP3]           BC`xOffset
        
        ;clear the flag for mob-collision-with-floor
        res     6       [IX`mob+%mob.flags]
        
        push    DE`yOffset
                HL`table
        
        ;check for mob collision with Floor:
        ;---------------------------------------------------------------------------------------------------------------
        
        ;lookup the Block the mob is within
        ;NOTE: `BC` is the pixel x-offset to apply, `DE` is the y-offset
        call    getFloorLayoutRAMAddressForMob
        ;read the Block index from the address returned,
        ;this tells us which Block the mob is within, though a Block is 4x4 Tiles / 32x32 pixels
        ld      E`block             [HL`floorLayout]
        ld      D`block             $00
        ;TODO: all this solidity pointer lookup could be done away with by storing this result
        ;      in RAM somewhere (or in the level header)
        ld      A`solidityID        [$.LEVEL_SOLIDITY]          ;solidity index for the level
        add     A`solidityID        A`solidityID                ;double it for a table look-up
        ld      C`index             A`solidityID                ;transfer value to BC
        ld      B`index             D`zero
        ;offset into the solidity table
        ld      HL`table            S1_Solidity_Blocks 
        add     HL`table            BC`index
        ;get the level's solidity pointer from the table
        ld      A`pointer           [HL`table]
        inc     HL`table
        ld      H`pointer           [HL`table]
        ld      L`pointer           A`pointer
        ;look up the block in the level's block solidity table
        add     HL`pointer          DE`block
        ld      A`blockSolidity     [HL`pointer]
        
        ;the top two bits of the line-solidity are used as flags
        ;- bit 7 : "totally solid", for faster collision checks
        ;- bit 6 : water?
        and     %00111111
        ;put aside the solidity flags for the Block that the mob is within
        ld      [$.TEMP6]           A`solidity
        
        pop     HL`table
                DE`yOffset
        
        and     %00111111                                       ;air (or water), and nothing else?
        ;if there's no flags remaining for this Block, no kind of collision can be possible, so skip ahead
        jp      z       ._7
        
        ;TODO: could we not have used B/C instead of [$.TEMP6] here?
        ld      A`solidity          [$.TEMP6]
        add     A`solidity          A`solidity                  ;double it for a look-up table
        ld      C`index             A`solidity                  ;transfer to BC
        ld      B`index             $00
        
        ;TODO: this might not be necessary, D could already be 0?
        ld      D`yOffset           B`zero
        
        ;offset into the lookup table, either "Unknown._411e" (facing right)
        ;or "Unknown._4020" (facing left)
        add     HL`table            BC`index
        ld      A`pointer           [HL`table]
        inc     HL`table    
        ld      H`pointer           [HL`table]
        ld      L`pointer           A`pointer
        
        ;the data is a per-line solidity lookup!
        ld      A`yPos              [IX`mob+%mob.Y+0]
        add     A`yPos              E`yOffset                   ;get the vertical middle point
        and     %00011111                                       ;"MOD 32", i.e. position within the Block
        ld      E`yPos              A`yPos
        add     HL`pointer          DE`yPos                     ;find the data for that particuar Block row
        
        ;bit 7 implies a solid line, collision is unavoidable
        ld      A`lineSolidity      [HL`table]
        cp      $80
        jp      z       ._7
        
        ;if the line is not solid, a collision may or may not occur depending on the exact position of the mob
        
        ld      E`lineSolidity      A`lineSolidity
        and     A`lineSolidity
        jp      p       ._3                                     ;skip if bit 7 is unset
        
        ld      D                   $FF
        
        ;here, D is $00 or $FF?
        
._3     ld      L`xPos              [IX`mob+%mob.X+0]
        ld      H`xPos              [IX`mob+%mob.X+1]
        ;retrieve the 'nose' position of the mob
        ld      BC`xOffset          [$.TEMP3]
        add     HL`xPos             BC`xOffset
        
        ;facing left or right?
        bit     7       [IX`mob+%mob.Xdirection]
        jr      nz      ._4
        
        ;facing left:
        and     A`lineSolidity
        jp      m       ._5                                     ;skip if bit 7 is set
        
        ld      A`xPos              L`xPos
        and     %00011111                                       ;"MOD 32"
        cp      E`lineSolidity
        jr      nc      ._5
        
        jp      ._7
        
        ;facing right:
._4     and     A`lineSolidity
        jp      m	._5                                         ;skip if bit 7 is set
        
        ld      A`xPos              L`xPos
        and     %00011111                                       ;"MOD 32"
        cp      E`lineSolidity
        jr      nc	._7
        
        ;collision:
        ;...............................................................................................................
        
        ;set the flag for mob collision with the Floor,
        ;this can simply mean that the mob is standing on the floor rather than "in air"/
._5     set     6       [IX`mob+%mob.flags]
        
        ;clip xPos to whole counts of 32 -- convert xPos to the left-nearest block
        ;(effectively "INT(xPos / 32) * 32")
        ld      A`xPos              L`xPos
        and     %11100000
        
        ld      L`xPos              A`xPos
        add     HL`xPos             DE`lineSolidity
        and     A`flags                                         ;clear carry flag
        sbc     HL`xPos             BC`xOffset
        ld      [IX`mob+%mob.X+0]   L`xPos
        ld      [IX`mob+%mob.X+1]   H`xPos
        
        ld      A`solidity          [$.TEMP6]
        ld      E`solidity          A`solidity
        ld      D`zero              $00
        ld      HL                  UnknownCollision._3FBF
        add     HL                  DE
        ld      C                   [HL]
        
        ld      [IX`mob+%mob.Xspeed+0]      D`zero
        ld      [IX`mob+%mob.Xspeed+1]      D`zero
        ld      [IX`mob+%mob.Xdirection]    D`zero
        ld      A                   D`zero
        ld      B                   D`zero
        
        bit     7   C
        jr      z	._6
        
        ;unused because of the data!?
        dec     A
        dec     B
        
._6     ld      L                   [IX`mob+%mob.Yspeed+0]
        ld      H                   [IX`mob+%mob.Yspeed+1]
        add     HL                  BC
        adc     A                   [IX`mob+%mob.Ydirection]
        ld      [IX`mob+%mob.Yspeed+0]      L
        ld      [IX`mob+%mob.Yspeed+1]      H
        ld      [IX`mob+%mob.Ydirection]    A
        
        ;no collision?
        ;...............................................................................................................
        
        ;zero the upper bytes of two sixteen bit words
._7     ld      B`zero              $00
        ld      D`zero              B`zero
        
        ;negative speed? i.e. is moving up
        bit     7       [IX`mob+%mob.Yspeed+1]
        jr      nz      ._8                                     ;skip the next bit if speed is negative
        
        ld      C                   [IX`mob+%mob.width]
        srl     C
        ld      E                   [IX`mob+%mob.height]
        ld      HL                  Unknown._448A
        jp      ._9
        
._8     ld      C                   [IX`mob+%mob.width]
        srl     C
        ld      E                   $00
        ld      HL                  Unknown._41EC
        
._9     ld      [$.TEMP3]           DE
        res     7   [IX`mob+%mob.flags]
        push    BC
        push    HL
        call    getFloorLayoutRAMAddressForMob
        ld      E                   [HL]
        ld      D                   $00
        ld      A                   [$.LEVEL_SOLIDITY]
        add     A                   A
        ld      C                   A
        ld      B                   D
        ld      HL                  S1_Solidity_Blocks
        add     HL                  BC
        ld      A                   [HL]
        inc     HL
        ld      H                   [HL]
        ld      L                   A
        add     HL                  DE
        ld      A                   [HL]
        and     $3F
        ld      [$.TEMP6]           A
        pop     HL
        pop     BC
        and     $3F
        jp      z	._34e6
        ld      A                   [$.TEMP6]
        add     A                   A
        ld      E                   A
        ld      D                   $00
        ld      B                   D
        add     HL                  DE
        ld      A                   [HL]
        inc     HL
        ld      H                   [HL]
        ld      L                   A
        ld      A                   [IX`mob+%mob.X+0]
        add     A                   C
        and     %00011111
        ld      C                   A
        add     HL                  BC
        ld      A                   [HL]
        cp      $80
        jp      z	._34e6
        ld      C                   A
        and     A
        jp      p	._10
        ld      B                   $FF
._10    ld      L                   [IX`mob+%mob.Y+0]
        ld      H                   [IX`mob+%mob.Y+1]
        ld      DE                  [$.TEMP3]
        add     HL                  DE
        bit     7   [IX`mob+%mob.Ydirection]
        jr      nz	._11
        and     A
        jp      m	._12                                        ;skip if bit 7 is set
        ld      A                   L
        and     %00011111
        exx     
        ld      HL                  [$.TEMP6]
        ld      H                   $00
        ld      DE                  UnknownCollision._3FF0
        add     HL                  DE
        add     A                   [HL]
        exx     
        cp      C
        jr      c	._34e6
        set     7   [IX`mob+%mob.flags]
        jp      ._12
        
._11    and     A
        jp      m	._12                                        ;skip if bit 7 is set
        ld      A                   L
        and     %00011111
        exx     
        ld      HL                  [$.TEMP6]
        ld      H                   $00
        ld      DE                  UnknownCollision._3FF0
        add     HL                  DE
        add     A                   [HL]
        exx     
        cp      C
        jr      nc	._34e6
._12    ld      A                   L
        and     $E0
        ld      L                   A
        add     HL                  BC
        and     A
        sbc     HL                  DE
        ld      [IX`mob+%mob.Y+0]   L
        ld      [IX`mob+%mob.Y+1]   H
        ld      A                   [$.TEMP6]
        ld      E                   A
        ld      D                   $00
        ld      HL                  $3F90                       ;data?
        add     HL                  DE
        ld      C                   [HL]
        ld      [IX`mob+%mob.Yspeed+0]      D
        ld      [IX`mob+%mob.Yspeed+1]      D
        ld      [IX`mob+%mob.Ydirection]    D
        ld      A                   D
        ld      B                   D
        bit     7   C
        jr      z	._13
        dec     A
        dec     B
._13    ld      L                   [IX`mob+%mob.Xspeed+0]
        ld      H                   [IX`mob+%mob.Xspeed+1]
        add     HL                  BC
        adc     A                   [IX`mob+%mob.Xdirection]
        ld      [IX`mob+%mob.Xspeed+0]      L
        ld      [IX`mob+%mob.Xspeed+1]      H
        ld      [IX`mob+%mob.Xdirection]    A
        
        ;is the mob on-screen?
        ;---------------------------------------------------------------------------------------------------------------

._34e6  ld      L`yPos              [IX`mob+%mob.Y+0]
        ld      H`yPos              [IX`mob+%mob.Y+1]
        ld      BC`cameraY          [$.CAMERA_Y]
        and     A`clearCarry                                    ;clear Carry before subtracting
        sbc     HL`yPos             BC`cameraY
        
        ex      DE                  HL
        
        ld      L`xPos              [IX`mob+%mob.X+0]
        ld      H`xpos              [IX`mob+%mob.X+1]
        ld      BC`cameraX          [$.CAMERA_X]
        and     A`clearCarry                                    ;clear Carry before subtracting
        sbc     HL                  BC
        
        ;if the mob has a sprite layout, update the mob's position on the screen
        ld      C                   [IX`mob+%mob.spriteLayout+0]
        ld      B                   [IX`mob+%mob.spriteLayout+1]
        ld      A                   C
        or      B
        call    nz  \\gfx.sprites\processSpriteLayout
        
        pop     HL
                BC
        
        ret
	;
	
processSpriteLayout:                                                                                            ;$350F
;=======================================================================================================================
```	Puts a mob on screen, combining multiple hardware sprites using a Sprite Layout - a list of sprites to arrange
        in a maximum 6 x 4 layout (note that each hardware sprite is 8 x 16, giving a maximum mob size of 48 x 64)
	```
@params IY`vars         ``Address of the common variables (used throughout)
        HL`xPos         ``X-position to place sprite layout on screen
        D               ;?? (some kind of control flag)
        E`ypos          ``Y-position to place sprite layout on screen
        BC`layoutDat    ``Address of a sprite layout
        ;---------------------------------------------------------------------------------------------------------------
        ;store the X-position of the sprite for aligning the rows
        ld      [$.TEMP6]       HL`xpos
        
        ;copy BC (address of a sprite layout) to its shadow value BC'
        push    BC`layoutData
        exx
        pop     BC'layoutData
        exx
        
        ;rows:
        ;---------------------------------------------------------------------------------------------------------------
        ;there will be 3 rows of double-high (16px) sprites
        ld      B`rows  0
        ld      C`rows  3
        
._1     exx	;-->                                            ;switch to BC/DE/HL shadow values
        
        ld      HL'xpos [$.TEMP6]                               ;get the starting X-position
                                                                ;(original HL parameter)
        
        ;if a row begins with $FF, the data ends early.
        ;begin a row with $FE to provide a space without ending the data early
        
        ld      A       [BC'layoutData]                         ;get a byte from the sprite layout data
        
        exx     ;<--                                            ;switch to original BC/DE/HL values
        
        cp      $FF                                             ;is the byte $FF?
        ret     z                                               ;if so leave
        
        ;DE is the Y-position, but if D is $FF then something else unknown happens
        
        ld      A       D                                       ;check the D parameter
        cp      $FF                                             ;if D is not $FF
        jr      nz      ._2                                     ;then skip ahead a little
        
        ld      A       E                                       ;check the E parameter
        cp      $F0                                             ;if it's less than $F0,
        jr      c       ._5                                     ;then skip ahead
        jp      ._3
        
._2     and     A                                               ;is the sprite byte 0?
        jr      nz      ._5
        
        ;exit if the row Y-position is below the screen
        ld      A       E
        cp      192
        ret     nc
        
        ;columns:
        ;---------------------------------------------------------------------------------------------------------------
._3     ;begin 6 columns of single-width (8px) sprites
        ld      B`cols	6
        
.loop   exx                                                     ;switch to BC/DE/HL shadow values
        
        ;has the X-position gone over 255?
        ld      A       H'                                      ;check the H parameter
        and     A                                               ;is it >0? i.e. HL = $0100
        jr      nz      ._4                                     ;if so skip
        
        ld      A       [BC']                                   ;check the current byte of the layout data
        cp      $FE                                             ;is it >= than $FE?
        jr      nc      ._4                                     ;if so, skip
        
        ;get the address of the sprite table entry
        ld      DE'     [$.SPRITETABLE_ADDR]        
        ld      A       L'                                      ;take the current X-position
        ld      [DE']   A                                       ;and set the sprite's X-position
        inc     E'                               
        exx
        ld      A       E                                       ;get the current Y-position
        exx
        ld      [DE']   A                                       ;set the sprite's Y-position 
        inc     E'
        ld      A       [BC']                                   ;read the layout byte
        ld      [DE']   A                                       ;set the sprite index number
        
        ;move to the next sprite table entry
        inc     E'
        ld      [$.SPRITETABLE_ADDR]    DE'        
        inc     [IY`vars+%vars.spriteUpdateCount]
        
        ;move across 8 pixels
._4     inc     BC'
        ld      DE'     $0008
        add     HL'     DE'
        
        ;return B to the column count and decrement
        exx
        djnz    .loop
        
        ;move down 16-pixels
        ld      A       C
        ex      DE      HL
        ld      C       16
        add     HL      BC
        ex      DE      HL
        
        ;any rows remaining?
        ld      C       A
        dec     C
        jr      nz      ._1
        ret
        
        ;---------------------------------------------------------------------------------------------------------------
        ;TODO: need to work this out (when D is $FF)
._5     exx
        ex      DE      HL
        ld      HL      $0006
        add     HL      BC
        ld      C       L
        ld      B       H
        ex      DE      HL
        exx
        ld      A       C
        ex      DE      HL
        ld      C       $10
        add     HL      BC
        ex      DE      HL
        ld      C       A
        dec     C
        jr      nz      ._1
        
        ret
	;
	
_3581:                                                                                                          ;$3581
;=======================================================================================================================
@params IY`vars         ``Address of the common variables (used throughout)
        $.TEMP3         ;y-position of some kind
        $.TEMP6         ;y-position of some kind
        $.TEMP1         ;x-position of some kind
        $.TEMP4         ;x-position of some kind
        ;---------------------------------------------------------------------------------------------------------------
        ld      HL      [$.TEMP3]
        ld      BC      [$.TEMP6]
        add     HL      BC
        ld      BC      [$.CAMERA_Y]
        and     A
        sbc     HL      BC
        
        ex      DE      HL
        
        ld      HL      [$.TEMP1]
        ld      BC      [$.TEMP4]
        add     HL      BC
        ld      BC      [$.CAMERA_X]
        and     A
        sbc     HL      BC
        ld      C       A
        ld      A       H
        and     A
        ret     nz
        
        ld      A       D
        cp      $FF
        jr      nz	._1
        ld      A       E
        cp      $F0
        ret     c
        jp      ._2
        
._1     and     A
        ret     nz
        ld      A       E
        cp      $C0
        ret     nc
._2     ld      H       C
        ld      BC      [$.SPRITETABLE_ADDR]
        ld      A       L
        ld      [BC]    A
        inc     C
        ld      A       E
        ld      [BC]    A
        inc     C
        ld      A       H
        ld      [BC]    A
        inc     C
        ld      [$.SPRITETABLE_ADDR]    BC
        inc     [IY`vars+%vars.spriteUpdateCount]
        ret
	;
	
layoutSpritesHorizontal:                                                                                        ;$35CC
;=======================================================================================================================
```     Places a set of sprites next to each other, dictated by a small data stream of indices, with $FE to leave a
        blank space and $FF to terminate. This routine is typically used to place text and numbers on screen.
	```
@params IY`vars         ``Address of the common variables (used throughout)
        B`ypos
        C`xpos
        HL`addr         ;($.SPRITETABLE_ADDR)  
        DE`data         ;$.LAYOUT_BUFFER : $A0, $A2, $A4, ($80 + $.LIVES * 2), $FF
        ;---------------------------------------------------------------------------------------------------------------
        ld      A`tileIndex, [DE`data]				;check the current byte in the list
        cp      $FF                                             ;is it an end marker? ($FF)
        ret     z                                               ;if so, return
        
        cp      $FE                                             ;special case for $FE command
        jr      z       .skip                                   ;(skip ahead)
        
        ld      [HL`addr]       C`xpos
        inc     L`addr
        ld      [HL`addr]       B`ypos
        inc     L`addr
        ld      [HL`addr]       A`tileIndex
        inc     L`addr
        
        inc     [IY`vars+%vars.spriteUpdateCount]
        
.skip   inc     DE`data                                         ;move to the next data byte
        ;move right 8 pixels
        ld      A`xpos  C`xpos
        add     A`xpos  8                       
        ld      C`xpos  A`xpos
        jp      :                                               ;process more sprites in the list
	;
	
hitPlayer:                                                                                                      ;$35E5
;=======================================================================================================================
@params IY`vars         ``Address of the common variables (used throughout)
        ;---------------------------------------------------------------------------------------------------------------
        bit     0   [IY`vars+%vars.scrollRingFlags]             ;is the player already dead?
        ret     nz                                              ;if so, leave now
        
        bit     0   [IY`vars+%vars.unknown0]
        jp      nz	_36be
        
        ld      A       [$.SONIC.flags]
        rrca    
        jp      c	_36be
        
        and     %00000010
        jp      nz	_36be
        
._35fd                                                                                                          ;$35FD
        ;---------------------------------------------------------------------------------------------------------------
        bit     0   [IY`vars+%vars.flags9]
        ret     nz
        
        bit     6   [IY`vars+%vars.flags6]                      ;is player in damage-state?
        ret     nz                                              ;if so, do not continue
        
        bit     0   [IY`vars+%vars.unknown0]
        ret     nz
        
        bit     5   [IY`vars+%vars.flags6]
        jr      nz	dropRings._367e
        
        ;has the player any rings?
        ld      A`rings         [$.RINGS]
        and     A`rings
        jr      nz	dropRings                                  ;if so, drop them
        
.kill   ;kill the player!                                                                                      `$3618
        ;---------------------------------------------------------------------------------------------------------------
        set     0   [IY`vars+%vars.scrollRingFlags]
        
        ;set flag 7 on the mob (mob death state?)
        ld      HL              $.SONIC.flags
        set     7               [HL]
        
        ld      HL              $FFFA
        xor     A`zero                                          ;set A to zero
        ld      [$.SONIC.Yspeed+0]             A`zero
        ld      [$.SONIC.Yspeed+1]             HL
        
        ld      A               $60
        ld      [$.D287]        A
        
        res     6   [IY`vars+%vars.flags6]                      ;turn off damage-state flag
        res     5   [IY`vars+%vars.flags6]                      ;remove shield
        res     6   [IY`vars+%vars.flags6]                      ;TODO: bug or oversight?
        res     0   [IY`vars+%vars.unknown0]                    ;the 0 byte from the level header
        
	;play the death sound effect:
	;(we can compile with, or without, audio)
	?( \\sound )
		ld	A`sfx	\\sound\music\pointers.death@index
		rst     \\sound\rst_playMusic
		;
	
        ret
	;
	
