getFloorLayoutRAMAddressForMob:                                                                                 ;$36F9
;=======================================================================================================================
        ;TODO: This whole thing seems highly inefficient
        
```	Retrieves an address in the Floor Layout in RAM based on the given mob's position.
        Note that each byte in the Floor Layout represents a 32x32 block (4x4 tiles).
```
@params IX`mob          ``Address of the mob to process
        BC`xOffset      ``Horizontal pixel offset to add to the mob's X position before locating tile
        DE`yOffset      ``Vertical pixel offset to add to the mob's Y position before locating tile
@return HL`addr         ``An address within the Floor Layout in RAM
        ;---------------------------------------------------------------------------------------------------------------
        ;how wide is the floor layout?
        ;TODO: we could do this check when loading a level and store the exact label to jump-to in RAM
        ld      A`floorWidth    [$.LEVEL_FLOORWIDTH]
        cp      128
        jr      z	.width128
        cp      64
        jr      z	.width64
        cp      32
        jr      z	.width32
        cp      16
        jr      z	.width16
        
        jp      .width256
        
        ;---------------------------------------------------------------------------------------------------------------
        ;128 block wide level:
        
.width128
        ld      L`mob_ypos      [IX`mob+%mob.Y+0]
        ld      H`mob_ypos      [IX`mob+%mob.Y+1]
        add     HL`mob_ypos     DE`yOffset
        ld      A               L
        add     A               A
        rl      H
        add     A               A
        rl      H
        and     %10000000
        ld      L               A
        ex      DE              HL
        ld      L               [IX`mob+%mob.X+0]
        ld      H               [IX`mob+%mob.X+1]
        add     HL              BC
        ld      A               L
        add     A               A
        rl      H
        add     A               A
        rl      H
        add     A               A
        rl      H
        ld      L               H
        ld      H               $00
        add     HL              DE
        ld      DE              $.FLOORLAYOUT
        add     HL              DE
        ret
        
        ;---------------------------------------------------------------------------------------------------------------
        ;64 block wide level:
        
.width64
        ld      L               [IX`mob+%mob.Y+0]
        ld      H               [IX`mob+%mob.Y+1]
        add     HL              DE
        ld      A               L
        add     A               A
        rl      H
        and     %11000000
        ld      L               A
        ex      DE              HL
        ld      L               [IX`mob+%mob.X+0]
        ld      H               [IX`mob+%mob.X+1]
        add     HL              BC
        ld      A               L
        add     A               A
        rl      H
        add     A               A
        rl      H
        add     A               A
        rl      H
        ld      L               H
        ld      H               $00
        add     HL              DE
        ld      DE              $.FLOORLAYOUT
        add     HL              DE
        ret
        
        ;---------------------------------------------------------------------------------------------------------------
        ;32 block wide level:
        
.width32
        ld      L               [IX`mob+%mob.Y+0]
        ld      H               [IX`mob+%mob.Y+1]
        add     HL              DE
        ld      A               L
        and     %11100000
        ld      L               A
        ex      DE              HL
        ld      L               [IX`mob+%mob.X+0]
        ld      H               [IX`mob+%mob.X+1]
        add     HL              BC
        ld      A               L
        add     A               A
        rl      H
        add     A               A
        rl      H
        add     A               A
        rl      H
        ld      L               H
        ld      H               $00
        add     HL              DE
        ld      DE              $.FLOORLAYOUT
        add     HL              DE
        ret
        
        ;---------------------------------------------------------------------------------------------------------------
        ;16 block wide level:
        
.width16
        ld      L               [IX`mob+%mob.Y+0]
        ld      H               [IX`mob+%mob.Y+1]
        add     HL              DE
        ld      A               L
        srl     H
        rra     
        and     %11110000
        ld      L               A
        ex      DE              HL
        ld      L               [IX`mob+%mob.X+0]
        ld      H               [IX`mob+%mob.X+1]
        add     HL              BC
        ld      A               L
        add     A               A
        rl      H
        add     A               A
        rl      H
        add     A               A
        rl      H
        ld      L               H
        ld      H               $00
        add     HL              DE
        ld      DE              $.FLOORLAYOUT
        add     HL              DE
        ret
        
.width256
        ;---------------------------------------------------------------------------------------------------------------
        ;level is 256 blocks wide:
        
        ld      L`mob_ypos      [IX`mob+%mob.Y+0]
        ld      H`mob_ypos      [IX`mob+%mob.Y+1]
        ;add the offset we've been given to aim at the "feet" of the mob
        add     HL`mob_ypos     DE`yOffset
        
        ;16-bit multiply by 8
        ld      A`ypos          L`mob_ypos
        rlca                                                    ;x2 ...
        rl      H`ypos                                          ;carries overflow into H
        rlca                                                    ;x4 ...
        rl      H`ypos
        rlca                                                    ;x8
        rl      H`ypos
        
        ;put Y-position aside into DE
        ex      DE`ypos         HL`yOffset
        
        ld      L`mob_xpos      [IX`mob+%mob.X+0]
        ld      H`mob_xpos      [IX`mob+%mob.X+1]
        ;add the offset we've been given to aim at the "feet" of the mob
        add     HL`mob_xpos     BC`xOffset
        
        ;16-bit multiply by 8
        ld      A`xpos          L`mob_xpos
        rlca                                                    ;x2 ...
        rl      H`xpos
        rlca                                                    ;x4 ...
        rl      H`xpos
        rlca                                                    ;x8
        rl      H`xpos
        
        ld      L`xpos          H`xpos        
        ld      H               $00
        ld      E               H
        add     HL              DE
        ld      DE`floorLayout  $.FLOORLAYOUT
        add     HL              DE`floorLayout
        ret
	;

updateSonicSpriteFrame:                                                                                         ;$37E0
;=======================================================================================================================
```	Copy the current Sonic animation frame into VRAM.
