        xor     A`zero
._14    ld      [IX`mob+%mob.unknown12] A
        ld      E       [IX`mob+%mob.Yspeed+0]
        ld      D       [IX`mob+%mob.Yspeed+1]
        ld      A       [IX`mob+%mob.Ydirection]
        and     A
        jp      p       ._15
        
