doCommand:
;=======================================================================================================================
        cp      $FF
        jp      z	cmdFF_stopMusic
        cp      $fE
        jp      z	cmdFE_stopSFX
        inc     DE
        ld      HL      commandPointers
        add     A       A
        ld      C       A
        ld      B       $00
        add     HL      BC
        ld      A       [HL]
        inc     HL
        ld      H       [HL]
        ld      L       A
        jp      [HL]
	;

cmdFF_stopMusic:
;=======================================================================================================================
        ld      L       [IX+%track.masterLoopAddress+0]
        ld      H       [IX+%track.masterLoopAddress+1]
        ld      A       L
        or      H
        jr      z	cmdFE_stopSFX.stopTrack
        ex      DE      HL
        jp      doUpdateTrack.trackReadLoop
	;

cmdFE_stopSFX:
;=======================================================================================================================
        xor     A
        ld      [$.SFXpriority] A

.stopTrack
        res     1   [IX+%track.flags]
        ld      A       %00001111
        or      [IX+%track.channelVolumePSG]
        out     [%sms.ports.psg]        A
        ret
