        ld      H       [IX+%track.loopAddress+1]
        ld      BC      $FFFB
        add     HL      BC
        ld      A       [HL]
        and     A
        jr      nz	.loopInit
        ld      A       [DE]
        dec     A
        jr      z	._2
        ld      [HL]    A
        jp      ._1

.loopInit
        dec     [HL]
        jr      z	._2
._1     ex      DE      HL
        inc     HL
        ld      A       [HL]
        inc     HL
        ld      H       [HL]
        ld      L       A
        ld      C       [IX+%track.baseAddress+0]
        ld      B       [IX+%track.baseAddress+1]
        add     HL      BC
        ex      DE      HL
        jp      doUpdateTrack.trackReadLoop
    
._2     ld      [IX+%track.loopAddress+0]       L
        ld      [IX+%track.loopAddress+1]       H
        inc     DE
        inc     DE
        inc     DE
        jp      doUpdateTrack.trackReadLoop
